      setDepositData(updatedDeposits);
    } catch (error) {
      console.error("Error creating deposit history:", error);
      Swal.fire({
        icon: "error",
        title: "Ïã§Ìå®",
        text: "Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
      });

    }
  };

  const handleDelete = async (depositId) => {
    Swal.fire({
      icon: "warning",
      title: "Ï†ïÎßê ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?",
      showCancelButton: true,
      confirmButtonText: "ÏÇ≠Ï†ú",
      cancelButtonText: "Ï∑®ÏÜå",
    }).then(async (result) => {
      if (result.isConfirmed) {
        try {
          await deleteDepositHistory(depositId);
          Swal.fire({
            icon: "success",
            title: "ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.",
          });
          const updatedDeposits = await fetchDepositHistoriesByCustomerId(userId);
          setDepositData(updatedDeposits);
        } catch (error) {
          console.error("Error deleting deposit history:", error);
          Swal.fire({
            icon: "error",
            title: "ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
          });
        }
      }
    });
  };

  return (
    <div className={styles.container}>
      <h2>Í±∞ÎûòÎÇ¥Ïó≠ Ï∂îÍ∞Ä (Í∏∞ÏóÖ Í∏∞Î°ùÏö©)</h2>
      <form onSubmit={handleSubmit}>
        {/* ÏÉÅÎã® ÏûÖÎ†•ÎûÄ: Í±∞ÎûòÏùºÏãú, Ï†ÅÏöî(description), Í∏∞Ïû¨ÎÇ¥Ïö©, ÎπÑÍ≥†(remarks) */}
        <div className={styles.infoContainer}>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Í±∞ÎûòÏùºÏãú</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="datetime-local"
                name="transactionDateTime"
                value={formData.transactionDateTime}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Ï†ÅÏöî</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Í∏∞Ïû¨ÎÇ¥Ïö©</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="details"
                value={formData.details}
                onChange={handleInputChange}
              />
            </div>
          </div>
        </div>

        {/* Ï§ëÎã® ÏûÖÎ†•ÎûÄ: Í≥ÑÏïΩÏûê, depositPhase1, Ï∞æÏúºÏã† Í∏àÏï°, Îß°Í∏∞Ïã† Í∏àÏï° */}
        <div className={styles.infoContainer}>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Í≥ÑÏïΩÏûê</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="contractor"
                value={formData.contractor}
                onChange={handleInputChange}
              />
            </div>
          </div>
          {/* depositPhase1 ÏûÖÎ†•ÎûÄ */}
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>1Ï∞®</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="depositPhase1"
                value={formData.depositPhase1}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Ï∞æÏúºÏã† Í∏àÏï°</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="withdrawnAmount"
                value={formData.withdrawnAmount}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Îß°Í∏∞Ïã† Í∏àÏï°</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="depositAmount"
                value={formData.depositAmount}
                onChange={handleInputChange}
              />
            </div>
          </div>
        </div>

        {/* ÌïòÎã® ÏûÖÎ†•ÎûÄ: Í±∞Îûò ÌõÑ ÏûîÏï°, Ï∑®Í∏âÏ†ê, Í≥ÑÏ¢å, ÎπÑÍ≥† */}
        <div className={styles.infoContainer}>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Í±∞Îûò ÌõÑ ÏûîÏï°</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="balanceAfter"
                value={formData.balanceAfter}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>Ï∑®Í∏âÏ†ê</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="text"
                name="branch"
