"use client";
import styles from "@/styles/Create.module.scss";
import Swal from "sweetalert2";
import {
  Inputbox,
  PostInputbox,
  InputAreabox,
  DropInputbox,
  FileInputbox,
  Checkbox,
  MGMInputbox,
} from "@/components/Inputbox";
import { Button_Y } from "@/components/Button";
import withAuth from "@/utils/hoc/withAuth";

import { useForm, Controller } from "react-hook-form";
import { useState, useEffect } from "react";
import {
  createFile,
  newIdGenerate,
  createUser,
  checkIdExists,
} from "@/utils/api";
import { useRouter } from "next/navigation";

import {
  banklist,
  classificationlist,
  typeidlist,
  typelist,
  grouplist,
  turnlist,
} from "@/components/droplistdata";

function Create() {
  const router = useRouter();

  const {
    register,
    handleSubmit,
    reset,
    setValue,
    watch,
    control,
    formState: { errors },
  } = useForm();

  const [newid, setNewid] = useState("");
  const [idExists, setIdExists] = useState(false);
  const [checkingId, setCheckingId] = useState(false);
  const [formattedRegisterPrice, setFormattedRegisterPrice] = useState("");
  const [formattedDepositAmmount, setFormattedDepositAmmount] = useState("");

  // ÌååÏùº ÏóÖÎ°úÎìú Ï≤¥ÌÅ¨Î∞ïÏä§ ÏÉÅÌÉú
  const [isupload, setIsupload] = useState({
    isuploaded: false,
    sealcertificateprovided: false,
    selfsignatureconfirmationprovided: false,
    commitmentletterprovided: false,
    idcopyprovided: false,
    freeoption: false,
    forfounding: false,
    generalmeetingconsentformprovided: false,
    preferenceattachment: false,
    prizeattachment: false,
    exemption7: false,
    investmentfile: false,
    contract: false,
  });

  const [file, setFile] = useState(null);

  // Ìú¥ÎåÄÌè∞ Î≤àÌò∏ "raw" Ïà´ÏûêÍ∞íÏùÑ Ï†ÄÏû• (ÌïòÏù¥Ìîà ÏóÜÏù¥ ÏµúÎåÄ 8ÏûêÎ¶¨)
  const [phoneDigits, setPhoneDigits] = useState("");
  // ÌôîÎ©¥Ïóê Î≥¥Ïó¨Ï§Ñ Í∞íÏùÄ phoneDigitsÎ•º Í∏∞Î∞òÏúºÎ°ú Í≥ÑÏÇ∞ (Ìï≠ÏÉÅ "010)" Ï†ëÎëêÏñ¥ Î∂ôÏûÑ)
  const computedPhoneDisplay =
    "010)" +
    (phoneDigits.length > 4
      ? phoneDigits.slice(0, 4) + "-" + phoneDigits.slice(4)
      : phoneDigits);

  useEffect(() => {
    // ÏÑúÎ≤ÑÏóêÏÑú Îã§Ïùå Í¥ÄÎ¶¨Î≤àÌò∏ Í∞ÄÏ†∏Ïò§Í∏∞
    const getData = async () => {
      try {
        const nextId = await newIdGenerate();
        setNewid(nextId);
        setValue("id", nextId);
      } catch (error) {
        console.error("Í¥ÄÎ¶¨Î≤àÌò∏Î•º Í∞ÄÏ†∏Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:", error);
      }
    };
    getData();
  }, [setValue]);

  /** Í¥ÄÎ¶¨Î≤àÌò∏ Ï§ëÎ≥µ Ï≤¥ÌÅ¨ */
  const handleIdChange = async (e) => {
    const enteredId = e.target.value;
    setValue("id", enteredId);

    if (enteredId) {
      setCheckingId(true);
      try {
        const exists = await checkIdExists(enteredId);
        setIdExists(exists);
      } catch (error) {
        console.error("Í¥ÄÎ¶¨Î≤àÌò∏ Ï§ëÎ≥µ Ï≤¥ÌÅ¨ Ïò§Î•ò:", error);
        setIdExists(true);
      } finally {
        setCheckingId(false);
      }
    } else {
      setIdExists(false);
    }
  };

  /** Ï≤¥ÌÅ¨Î∞ïÏä§ Î≥ÄÌôî */
  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setIsupload((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  /** ÌååÏùº ÏóÖÎ°úÎìú Î≥ÄÌôî */
  const handleFileChange = (e) => {
    const { files } = e.target;
    if (files && files.length > 0) {
      const selectedFile = files[0];
      setFile(selectedFile);
      setIsupload((prev) => ({
        ...prev,
        isuploaded: true,
      }));
    }
  };

  /** Ïà´Ïûê Ï≤úÎã®ÏúÑ ÏΩ§Îßà Ï≤òÎ¶¨ */
  const formatNumberWithCommas = (value) => {
    const numericValue = value.replace(/[^0-9]/g, "");
    if (!numericValue) return "";
    return parseInt(numericValue, 10).toLocaleString();
  };

  const handleRegisterPriceChange = (e) => {
    const formattedValue = formatNumberWithCommas(e.target.value);
    setFormattedRegisterPrice(formattedValue);
    const rawValue = formattedValue.replace(/,/g, "");
    setValue("registerprice", rawValue ? parseInt(rawValue, 10) : null);
  };

  const handleDepositAmmountChange = (e) => {
    const formattedValue = formatNumberWithCommas(e.target.value);
    setFormattedDepositAmmount(formattedValue);
    const rawValue = formattedValue.replace(/,/g, "");
    setValue("deposits.depositammount", rawValue ? parseInt(rawValue, 10) : null);
  };

  /**
   * Ìú¥ÎåÄÌè∞ Î≤àÌò∏ Ìè¨Îß∑ÌåÖ Ìï∏Îì§Îü¨  
   * e.target.value ÏóêÏÑú "010)" Ï†ëÎëêÏñ¥Î•º Ï†úÍ±∞ÌïòÍ≥†, Ïà´ÏûêÎßå Ï∂îÏ∂úÌïòÏó¨ ÏµúÎåÄ 8ÏûêÎ¶¨ÍπåÏßÄ Ï†ÄÏû•Ìïú ÌõÑ,
   * ÌôîÎ©¥ÏóêÎäî "010)" + (Ïà´ÏûêÍ∞Ä 4ÏûêÎ¶¨ Ïù¥ÏÉÅÏù¥Î©¥ Ï§ëÍ∞ÑÏóê ÌïòÏù¥Ìîà Ï∂îÍ∞Ä) Î°ú ÌëúÏãúÌï®.
   */
  const handlePhoneNumberChange = (e, onChange) => {
    let input = e.target.value;
    // ÎßåÏïΩ ÏûÖÎ†•Í∞íÏóêÏÑú "010)" Ï†ëÎëêÏñ¥Í∞Ä ÏûàÎã§Î©¥ Ï†úÍ±∞
    if (input.startsWith("010)")) {
      input = input.slice(4);
    }
    // Ïà´ÏûêÎßå Ï∂îÏ∂ú
    let digits = input.replace(/\D/g, "");
    // ÏµúÎåÄ 8ÏûêÎ¶¨ÍπåÏßÄ
    digits = digits.substring(0, 8);
    setPhoneDigits(digits);
    onChange(digits);
  };

  /** Ìèº Í≤ÄÏ¶ù Ïò§Î•ò Ï≤òÎ¶¨ */
  const onError = (errors) => {
    console.log("Í≤ÄÏ¶ù Ïò§Î•ò:", errors);

    const errorMessages = [];
    for (const field in errors) {
      if (errors.hasOwnProperty(field)) {
        if (errors[field].message) {
          errorMessages.push(errors[field].message);
        } else if (typeof errors[field] === "object" && errors[field] !== null) {
          for (const subField in errors[field]) {
            if (
              errors[field].hasOwnProperty(subField) &&
              errors[field][subField].message
            ) {
              errorMessages.push(errors[field][subField].message);
            }
          }
        }
      }
    }

    Swal.fire({
      icon: "warning",
      title: "ÌïÑÏàò Ìï≠Î™© ÎàÑÎùΩ",
      text: errorMessages.join("\n"),
    });
  };

  /** Ìèº Ï†úÏ∂ú */
  const onSubmit = async (data) => {
    try {
      // ÌååÏùº ÏóÖÎ°úÎìú Ï≤òÎ¶¨
      let uploadedFileInfo = "";
      if (file) {
        const uploadResponse = await createFile(file, parseInt(data.id, 10));
        uploadedFileInfo = uploadResponse.data;
      }

      // Ïà´Ïûê Î≥ÄÌôò
      const parsedData = {
        ...data,
        id: parseInt(data.id, 10),
        registerprice: parseInt(data.registerprice, 10),
        CustomerData: {
          ...data.CustomerData,
          resnumfront: parseInt(data.CustomerData.resnumfront, 10),
          resnumback: parseInt(data.CustomerData.resnumback, 10),
        },
        deposits: {
          depositdate: data.deposits.depositdate,
          depositammount: parseInt(data.deposits.depositammount, 10),
        },
      };

      // Ï≤®Î∂Ä ÌååÏùº Ï†ïÎ≥¥
      const attachments = {
        ...isupload,
        fileinfo: uploadedFileInfo,
        prizename: data.prizename,
        prizedate: data.prizedate,
      };

      // ÏµúÏ¢Ö Ï†ÑÏÜ°Ìï† Îç∞Ïù¥ÌÑ∞
      const customerData = {
        id: parsedData.id,
        customertype: data.customertype,
        type: data.type,
        groupname: data.groupname,
        turn: data.turn,
        batch: data.batch,
        registerdate: parsedData.registerdate,
        registerprice: parsedData.registerprice,
        registerpath: data.registerpath,
        additional: data.additional,
        prizewinning: data.prizewinning,
        votemachine: data.votemachine,
        CustomerData: parsedData.CustomerData,
        LegalAddress: data.LegalAddress,
        Postreceive: data.Postreceive,
        Financial: {
          ...data.Financial,
        },
        deposits: parsedData.deposits,
        attachments: attachments,
        dahim: data.dahim,
        MGM: data.MGM,
        firstemp: data.firstemp,
        secondemp: data.secondemp,
        responsible: data.responsible,
        meetingattend: data.meetingattend,
