  // ÏóëÏÖÄ ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Ìï∏Îì§Îü¨
  const handleExport = () => {
    if (depositData.length === 0) {
      Swal.fire({
             icon: "warning",
              title: "ÎÇ¥Î≥¥ÎÇº Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.",
              text: "Îç∞Ïù¥ÌÑ∞Í∞Ä Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏïÑ ÏóëÏÖÄÎ°ú ÎÇ¥Î≥¥ÎÇº Ïàò ÏóÜÏäµÎãàÎã§.",
            });
      return;
    }

    // Ï†ïÎ†¨Îêú Îç∞Ïù¥ÌÑ∞Î•º Í∏∞Ï§ÄÏúºÎ°ú ÏóëÏÖÄ ÏÉùÏÑ±
    const sortedData = [...depositData];
    if (sortConfig.key) {
      sortedData.sort((a, b) => {
        let aValue, bValue;
        if (sortConfig.key === "computedDate") {
          aValue = a.loanRecord
            ? a.loanDetails?.loandate
              ? new Date(a.loanDetails.loandate)
              : new Date(0)
            : a.selfRecord && !isNaN(Number(a.selfRecord)) && a.loanDetails?.selfdate
            ? new Date(a.loanDetails.selfdate)
            : new Date(0);
          bValue = b.loanRecord
            ? b.loanDetails?.loandate
              ? new Date(b.loanDetails.loandate)
              : new Date(0)
            : b.selfRecord && !isNaN(Number(b.selfRecord)) && b.loanDetails?.selfdate
            ? new Date(b.loanDetails.selfdate)
            : new Date(0);
        } else {
          aValue = a[sortConfig.key];
          bValue = b[sortConfig.key];

          if (aValue === null || aValue === undefined) return 1;
          if (bValue === null || bValue === undefined) return -1;

          if (sortConfig.key === "transactionDateTime") {
            aValue = new Date(aValue);
            bValue = new Date(bValue);
          }

          if (typeof aValue === "string") {
            aValue = aValue.toLowerCase();
            bValue = bValue.toLowerCase();
          }
        }

        if (aValue < bValue) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }

    // ÏóëÏÖÄÏóê ÎÑ£ÏùÑ Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
    const exportData = sortedData.map((item) => {
      let dateValue = "N/A";
