        dateValue = item.loanDetails?.selfdate
          ? new Date(item.loanDetails.selfdate).toLocaleDateString()
          : "N/A";
      }
      return {
        "ID": item.id || "N/A",
        "Í±∞ÎûòÏùºÏãú": item.transactionDateTime
          ? new Date(item.transactionDateTime).toLocaleString()
          : "N/A",
        "Ï†ÅÏöî": item.description || "",
        "Í∏∞Ïû¨ÎÇ¥Ïö©": item.details || "",
        "Í≥ÑÏïΩÏûê": item.contractor || "N/A",
        "Ï∞æÏúºÏã†Í∏àÏï°": item.withdrawnAmount
          ? item.withdrawnAmount.toLocaleString()
          : "0",
        "Îß°Í∏∞Ïã†Í∏àÏï°": item.depositAmount
          ? item.depositAmount.toLocaleString()
          : "0",
        "Í±∞Îûò ÌõÑ ÏûîÏï°": item.balanceAfter
          ? item.balanceAfter.toLocaleString()
          : "0",
        "Ï∑®Í∏âÏ†ê": item.branch || "",
        "Í≥ÑÏ¢å": item.account || "",
        "1Ï∞®": item.depositPhase1 || "",
        "2Ï∞®": item.depositPhase2 || "",
        "3Ï∞®": item.depositPhase3 || "",
        "4Ï∞®": item.depositPhase4 || "",
        "5Ï∞®": item.depositPhase5 || "",
        "6Ï∞®": item.depositPhase6 || "",
        "7Ï∞®": item.depositPhase7 || "",
        "8Ï∞®": item.depositPhase8 || "",
        "9Ï∞®": item.depositPhase9 || "",
        "10Ï∞®": item.depositPhase10 || "",
        "loan_record": item.loanRecord || "",
        "self_record": item.selfRecord || "",
        "ÏùºÏûê": dateValue,
        "ÎπÑÍ≥†": item.remarks || "",
      };
    });

    const worksheet = XLSX.utils.json_to_sheet(exportData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Deposit");

    const excelBuffer = XLSX.write(workbook, {
      bookType: "xlsx",
      type: "array",
    });
    const data = new Blob([excelBuffer], {
      type:
        "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8",
    });

    // ÌòÑÏû¨ ÎÇ†ÏßúÎ•º "YYYYMMDD" ÌòïÏãùÏúºÎ°ú Ìè¨Îß∑ÌåÖ
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = String(currentDate.getMonth() + 1).padStart(2, "0");
    const day = String(currentDate.getDate()).padStart(2, "0");
    const formattedDate = `${year}${month}${day}`;

    saveAs(data, `Deposit_${formattedDate}.xlsx`);
  };

  // Îç∞Ïù¥ÌÑ∞ ÌéòÏπ≠
  useEffect(() => {
    const getData = async () => {
      try {
        const data = await fetchDepositList();
        setDepositData(data);
      } catch (error) {
        console.error("ÏûÖÍ∏à ÎÇ¥Ïó≠ÏùÑ Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.", error);
      }
    };
    getData();
  }, []);

  return (
    <>
      <DepositForm
        contractor={contractor}
        setContractor={setContractor}
        memberNumber={memberNumber}
        setMemberNumber={setMemberNumber}
        onExport={handleExport} // ÏóëÏÖÄ Ï∂úÎ†• Î≤ÑÌäº Ìï∏Îì§Îü¨ Ï†ÑÎã¨
      />

      <DepositList
        contractor={contractor}
        memberNumber={memberNumber}
        setDepositData={setDepositData}
        sortConfig={sortConfig}
        setSortConfig={setSortConfig}
      />
    </>
  );
}

