      <div className={styles.infoContainer}>
        <div className={styles.unitbody}>
          <div className={styles.titlebody}>
            <span className={styles.title}>Í¥ÄÎ¶¨Î≤àÌò∏</span>
          </div>
          <div className={styles.contentbody}>
            <span>{userId || "N/A"}</span>
          </div>
        </div>
      </div>
      <form onSubmit={handleSubmit}>
        {/* Ï≤´ Î≤àÏß∏ Ìñâ: ÎåÄÏ∂úÏùºÏûê / ÎåÄÏ∂úÏï° / ÎåÄÏ∂úÏùÄÌñâ */}
        <div className={styles.infoContainer}>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>ÎåÄÏ∂úÏùºÏûê</label>
            </div>
            <div className={styles.contentbody}>
              <InputboxGray
                type="datetime-local"
                name="loandate"
                value={loanData.loandate}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className={styles.unitbody}>
            <div className={styles.titlebody}>
              <label className={styles.title}>ÎåÄÏ∂úÏï°</label>
            </div>
            <div className={styles.contentbody}>
