import LateFeeList from "@/components/LateFeeList";
import { saveAs } from "file-saver";
import * as XLSX from "xlsx";
import styles from "@/styles/Latefees.module.scss"; // Ïä§ÌÉÄÏùº ÏûÑÌè¨Ìä∏
import categoryMapping from "@/utils/categoryMapping";
import { FaPrint } from "react-icons/fa6"; // FaPrint ÏïÑÏù¥ÏΩò ÏûÑÌè¨Ìä∏

function LateFees() {
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");
  const [lateFees, setLateFees] = useState([]);
  const [sortConfig, setSortConfig] = useState({
    key: null,
    direction: "ascending",
  });

  // ÏóëÏÖÄ ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Ìï∏Îì§Îü¨
  const handleExport = () => {
    if (lateFees.length === 0) {
      alert("ÎÇ¥Î≥¥ÎÇº Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }

    // Ï†ïÎ†¨Îêú Îç∞Ïù¥ÌÑ∞Î•º Í∏∞Ï§ÄÏúºÎ°ú ÏóëÏÖÄ ÏÉùÏÑ±
    const sortedData = [...lateFees];
    if (sortConfig.key) {
      sortedData.sort((a, b) => {
        let aValue = a[sortConfig.key];
        let bValue = b[sortConfig.key];

        // null ÎòêÎäî undefined Ï≤òÎ¶¨
        if (aValue === null || aValue === undefined) return 1;
        if (bValue === null || bValue === undefined) return -1;

        if (typeof aValue === "string") {
          aValue = aValue.toLowerCase();
          bValue = bValue.toLowerCase();
        }

        if (aValue < bValue) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }

    // ÏóëÏÖÄÏóê ÎÑ£ÏùÑ Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
    const exportData = sortedData.map((fee) => ({
      ID: fee.id,
      "ÎßàÏßÄÎßâ ÎØ∏ÎÇ© Ï∞®Ïàò": fee.lastUnpaidPhaseNumber || "ÏóÜÏùå",
      Î∂ÑÎ•ò: categoryMapping[fee.customertype] || "N/A",
      ÏÑ±Î™Ö: fee.name || "N/A",
      Í∞ÄÏûÖÏùº: fee.registerdate ? fee.registerdate.slice(0, 10) : "N/A",
      Ïó∞Ï≤¥Í∏∞Ï§ÄÏùº: fee.lateBaseDate ? fee.lateBaseDate.slice(0, 10) : "N/A",
      ÏµúÍ∑ºÎÇ©Î∂Ä: fee.recentPaymentDate
        ? fee.recentPaymentDate.slice(0, 10)
        : "N/A",
      ÏùºÏàò: fee.daysOverdue,
      "Ïó∞Ï≤¥Ïú® (%)": fee.lateRate.toFixed(2),
      Ïó∞Ï≤¥Í∏àÏï°: fee.overdueAmount,
      ÎÇ©ÏûÖÍ∏àÏï°: fee.paidAmount,
      Ïó∞Ï≤¥Î£å: fee.lateFee,
      "ÎÇ¥ÏïºÌï† Îèà Ìï©Í≥Ñ": fee.totalOwed,
    }));

    const worksheet = XLSX.utils.json_to_sheet(exportData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "LateFees");

    const excelBuffer = XLSX.write(workbook, {
      bookType: "xlsx",
      type: "array",
    });
    const data = new Blob([excelBuffer], {
      type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8",
    });

    // ÌòÑÏû¨ ÎÇ†ÏßúÎ•º "YYYYMMDD" ÌòïÏãùÏúºÎ°ú Ìè¨Îß∑ÌåÖ
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = String(currentDate.getMonth() + 1).padStart(2, "0");
    const day = String(currentDate.getDate()).padStart(2, "0");
    const formattedDate = `${year}${month}${day}`;

    saveAs(data, `LateFees_${formattedDate}.xlsx`);
  };

  return (
    <>
      <div className={styles.headerContainer}>
        <h3> </h3>
      </div>
      <SearchForm
        name={name}
        setName={setName}
        number={number}
        setNumber={setNumber}
      />
      <h3></h3>
      <div className={styles.exportContainer}>
          <FaPrint
            onClick={handleExport}
            style={{color:"#5c41c2"}}
            className={styles.printIcon}
            title="ÏóëÏÖÄÎ°ú Ï∂úÎ†•" 
          />
          <button onClick={handleExport} className={styles.exportButton}>
            ÏóëÏÖÄÎ°ú Ï∂úÎ†•
          </button>
        </div>
      <LateFeeList
        name={name}
        number={number}
        linkBase="/latefees/"
        setLateFees={setLateFees}
        sortConfig={sortConfig}
        setSortConfig={setSortConfig}
      />
    </>
  );
}

