// Ïà´Ïûê Ï≤úÎã®ÏúÑ ÏΩ§Îßà Ï≤òÎ¶¨ Ìï®Ïàò
function formatNumberWithComma(num) {
  if (num == null || isNaN(num)) return "0";
  return num.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
}

export default function OverduePage() {
  const params = useParams();
  const phasenumber = params.phasenumber;
  const searchParams = useSearchParams();

  const overdueAmount = searchParams.get("amount")
    ? parseFloat(searchParams.get("amount"))
    : 0;
  const userid = searchParams.get("userid") || "Ï†ïÎ≥¥ ÏóÜÏùå";
  const name = searchParams.get("name") || "Ï†ïÎ≥¥ ÏóÜÏùå";

  // Ïó∞Ï≤¥Ïú® 8%
  const lateRate = 0.08;
  const today = new Date();

  // 30ÏùºÎ∂ÄÌÑ∞ 1ÏùºÍπåÏßÄ ÎÇ¥Î¶ºÏ∞®Ïàú ÎÇ†Ïßú Î¶¨Ïä§Ìä∏ ÏÉùÏÑ±
  const daysArray = Array.from({ length: 30 }, (_, i) => 30 - i); 
  // daysArray = [30, 29, 28, ..., 1]

  const data = useMemo(() => {
    let previousOverdueFee = overdueAmount * lateRate; // Ï≤´ ÎÇ†(30Ïùº ÌõÑ) Ïó∞Ï≤¥Î£å
    const result = [];
    // Ï≤´ ÎÇ†(Í∞ÄÏû• Î®º ÎÇ†Ïßú): overdueFee = overdueAmount * lateRate
    // ÎëòÏß∏ ÎÇ†Î∂ÄÌÑ∞: overdueFee = previousOverdueFee * (1 + lateRate)
    for (let i = 0; i < daysArray.length; i++) {
      const day = daysArray[i];
      const date = new Date(today);
      date.setDate(date.getDate() + day);

      let overdueFee;
      if (i === 0) {
        // Ï≤´ ÎÇ†
        overdueFee = previousOverdueFee;
      } else {
        // Îëê Î≤àÏß∏ ÎÇ†Î∂ÄÌÑ∞ Ï†ÑÎÇ† Ïó∞Ï≤¥Î£å * (1 + lateRate)
        overdueFee = previousOverdueFee * (1 + lateRate);
        previousOverdueFee = overdueFee; // Îã§ÏùåÎÇ† Í≥ÑÏÇ∞ÏùÑ ÏúÑÌï¥ ÏóÖÎç∞Ïù¥Ìä∏
      }

      // ÎÇ©Î∂ÄÍ∏àÏï° = Ïó∞Ï≤¥Í∏àÏï° + ÎãπÏùº Ïó∞Ï≤¥Î£å
      const totalPayment = overdueAmount + overdueFee;

      result.push({
        ÎÇ†Ïßú: date.toISOString().slice(0, 10),
        ÏùºÏàò: `${day}Ïùº`,
        Ïó∞Ï≤¥Ïú®: `${(lateRate * 100).toFixed(2)}%`,
        Ïó∞Ï≤¥Î£å: Math.round(overdueFee),
        ÎÇ©Î∂ÄÍ∏àÏï°: Math.round(totalPayment),
      });
    }

    return result;
  }, [overdueAmount, lateRate, today]);

  // ÏóëÏÖÄ ÎÇ¥Î≥¥ÎÇ¥Í∏∞ Ìï∏Îì§Îü¨
  const handleExport = () => {
    if (data.length === 0) {
      alert("ÎÇ¥Î≥¥ÎÇº Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }

    // ÏóëÏÖÄÏóê ÎÑ£ÏùÑ Îç∞Ïù¥ÌÑ∞ Ìè¨Îß∑ÌåÖ
    const worksheet = XLSX.utils.json_to_sheet(data);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "OverdueFees");

    const excelBuffer = XLSX.write(workbook, {
      bookType: "xlsx",
      type: "array",
    });
    const blob = new Blob([excelBuffer], {
      type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet;charset=UTF-8",
    });

    // ÌòÑÏû¨ ÎÇ†ÏßúÎ•º "YYYYMMDD" ÌòïÏãùÏúºÎ°ú Ìè¨Îß∑ÌåÖ
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = String(currentDate.getMonth() + 1).padStart(2, "0");
    const day = String(currentDate.getDate()).padStart(2, "0");
    const formattedDate = `${year}${month}${day}`;

    saveAs(blob, `Overdue_${userid}_${phasenumber}_${formattedDate}.xlsx`);
  };

  return (
    <div style={{ padding: "20px" }}>
      <div style={headerContainerStyle}>
        <h1>ÏùºÏûêÎ≥Ñ Ïó∞Ï≤¥Î£å ({phasenumber}Ï∞®)</h1>
        <button
          style={exportButtonStyle}
          onClick={handleExport}
          title="ÏóëÏÖÄÎ°ú Ï∂úÎ†•"
        >
          ÏóëÏÖÄÎ°ú Ï∂úÎ†•
        </button>
      </div>
      <p>Í¥ÄÎ¶¨Î≤àÌò∏: {userid}</p>
      <p>Ïù¥Î¶Ñ: {name}</p>
      <p>Ïó∞Ï≤¥Í∏àÏï°: {formatNumberWithComma(overdueAmount)}Ïõê</p>

      <table style={{ borderCollapse: "collapse", width: "100%" }}>
        <thead>
          <tr>
            <th style={thStyle}>ÎÇ†Ïßú</th>
            <th style={thStyle}>ÏùºÏàò</th>
            <th style={thStyle}>Ïó∞Ï≤¥Ïú®</th>
            <th style={thStyle}>Ïó∞Ï≤¥Î£å</th>
            <th style={thStyle}>ÎÇ©Î∂ÄÍ∏àÏï°</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, i) => (
            <tr key={i}>
              <td style={tdStyle}>{row.ÎÇ†Ïßú}</td>
              <td style={tdStyle}>{row.ÏùºÏàò}</td>
              <td style={tdStyle}>{row.Ïó∞Ï≤¥Ïú®}</td>
              <td style={tdStyle}>{formatNumberWithComma(row.Ïó∞Ï≤¥Î£å)}Ïõê</td>
              <td style={tdStyle}>{formatNumberWithComma(row.ÎÇ©Î∂ÄÍ∏àÏï°)}Ïõê</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

// Ïä§ÌÉÄÏùº Í∞ùÏ≤¥
const headerContainerStyle = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "20px",
