import { useRouter } from "next/navigation"; // Import useRouter
import Swal from "sweetalert2"; // Import SweetAlert2
import { fetchSignup } from "@/utils/api";

export default function Signup() {
  const [userform, setUserform] = useState({
    username: "",
    email: "",
    password: "",
    roles: ["user"], //default Îäî Ïú†Ï†Ä
  });

  const router = useRouter(); // Initialize useRouter

  const handleChange = (e) => {
    setUserform({
      ...userform,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetchSignup(
        userform.username,
        userform.email,
        userform.password,
        userform.roles
      );
      Swal.fire({
        title: "ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ",
        text: "ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!",
        icon: "success",
        confirmButtonText: "ÌôïÏù∏",
      }).then(() => {
        router.push("/dashboard"); // Redirect to dashboard
      });
      console.log("Signup successful:", response.data);
    } catch (error) {
      console.error("Signup error:", error.response.data.message);
      Swal.fire({
        title: "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®",
        text: error.response.data.message || "ÌöåÏõêÍ∞ÄÏûÖÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.",
        icon: "error",
        confirmButtonText: "ÌôïÏù∏",
      });
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.childcontainer}>
        <h1 className={styles.title}>ÌöåÏõêÍ∞ÄÏûÖ</h1>
        <form className={styles.form} onSubmit={handleSubmit}>
          <LoginInputbox
            className={styles.input}
            name="username"
            placeholder="ÏÇ¨Ïö©Ïûê Ïù¥Î¶Ñ (ÏïÑÏù¥Îîî)"
            onChange={handleChange}
          />
          <LoginInputbox
            className={styles.input}
            name="email"
            placeholder="Ïù¥Î©îÏùº"
            type="email"
            onChange={handleChange}
          />
          <LoginInputbox
            className={styles.input}
            name="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            type="password"
            onChange={handleChange}
          />
          <Button className={styles.button} type="submit">
            ÌöåÏõêÍ∞ÄÏûÖ
          </Button>
