              <div className={styles.CBTDate}>
                <div className={styles.CBTDateFont}>
                  ÏòàÏ†ïÏùºÏûê: {getDisplayPlannedDate(item) || "N/A"}
                </div>
              </div>
            </div>
          </div>
          <div className={styles.CBBottonBody}>
            <ModifyButton onClick={() => {}}>
              <Link href={`/inputmoney/payinfo/${userId}/${item.phaseNumber}`}>
                <div className={styles.CBBottonFont}>ÎÇ©Î∂ÄÏàòÏ†ï</div>
              </Link>
            </ModifyButton>
          </div>
          <div className={styles.CBSum}>
            <div className={styles.CBMoneyImg}>
              <div className={styles.Icon2}>
                <BsDatabase style={{ width: "100%", height: "100%" }} />
              </div>
            </div>
            <div className={styles.CBSumText}>{item.phaseNumber}Ï∞® Ï¥ùÏï°</div>
            <div className={styles.CBSumNum}>
              {item.feesum ? item.feesum.toLocaleString() : 0} ‚Ç©
            </div>
          </div>
          <div className={styles.CBSum}>
            <div className={styles.CBMoneyImg}>
              <div className={styles.Icon2}>
                <BsDatabase style={{ width: "100%", height: "100%" }} />
              </div>
            </div>
            <div className={styles.CBSumText}>ÎÇ©ÏûÖÍ∏àÏï°</div>
            <div className={styles.CBSumNum}>
