        icon: "success",
        title: "ÌöåÏõê Ìï¥ÏßÄ",
        text: "ÌöåÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ìï¥ÏßÄÎêòÏóàÏäµÎãàÎã§.",
        confirmButtonText: "ÌôïÏù∏",
      }).then(() => {
        const updatedFees = localLateFees.filter((fee) => fee.id !== id);
        setLocalLateFees(updatedFees);
        setLateFees(updatedFees);
      });
    } catch (error) {
      console.error("Error cancelling customer:", error);
      Swal.fire({
        icon: "error",
        title: "Ìï¥ÏßÄ Ïã§Ìå®",
        text: "ÌöåÏõê Ìï¥ÏßÄ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.",
        confirmButtonText: "ÌôïÏù∏",
      });
    }
  };

  // Î™®Îã¨ Ïó¥Í∏∞
  const openConfirmation = (id) => {
    setSelectedCustomerId(id);
    setIsModalOpen(true);
  };

  // Î™®Îã¨ ÌôïÏù∏ -> Ìï¥ÏßÄ ÏßÑÌñâ
