// src/components/SearchList.js

"use client";

import Link from "next/link";
import { useEffect, useState } from "react";
import { useRecoilValueLoadable, useSetRecoilState } from "recoil";
import { ModifyButton } from "@/components/Button";
import styles from "@/styles/Search.module.scss";
import { searchnameState, searchnumberState } from "@/utils/atom";
import { namesearchSelector } from "@/utils/selector";
import { deleteCustomer } from "@/utils/api";
import categoryMapping from "@/utils/categoryMapping";
import ConfirmationModal from "@/components/ConfirmationModal";
import Swal from "sweetalert2";

// react-iconsÏóêÏÑú ÏïÑÏù¥ÏΩò ÏûÑÌè¨Ìä∏
import { AiOutlineArrowUp, AiOutlineArrowDown } from "react-icons/ai";

// droplistdataÏóêÏÑú Î¶¨Ïä§Ìä∏Îì§ÏùÑ ÏûÑÌè¨Ìä∏
import {
  typelist,
  grouplist,
  turnlist,
  typeidlist,
  classificationlist,
} from "@/components/droplistdata";

const SearchList = ({ name, number, categoryFilter, linkBase }) => {
  const setNameState = useSetRecoilState(searchnameState);
  const setNumberState = useSetRecoilState(searchnumberState);
  const [sortConfig, setSortConfig] = useState({
    key: null,
    direction: "ascending",
  });

  // ÌïÑÌÑ∞ÎßÅ ÏÉÅÌÉúÎ•º Í¥ÄÎ¶¨ÌïòÎäî state
  const [filters, setFilters] = useState({
    type: [],
    groupname: [],
    turn: [],
    batch: [],
    customertype: [],
  });

  // ÎìúÎ°≠Îã§Ïö¥ Î©îÎâ¥Ïùò Ïó¥Î¶º ÏÉÅÌÉúÎ•º Í¥ÄÎ¶¨ÌïòÎäî state
  const [dropdownOpen, setDropdownOpen] = useState({
    type: false,
    groupname: false,
    turn: false,
    batch: false,
    customertype: false,
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedCustomerId, setSelectedCustomerId] = useState(null);

  let searchname = name.length > 1 ? name : "";
  let searchnumber = number.length > 1 ? number : "";

  useEffect(() => {
    setNameState(searchname);
    setNumberState(searchnumber);
  }, [searchname, searchnumber, setNameState, setNumberState]);

  const searchdata = useRecoilValueLoadable(namesearchSelector);

  // Ï°∞Í±¥Î∂Ä Î†åÎçîÎßÅÏùÑ ÏúÑÌïú Î≥ÄÏàò (Î™®Îì† ÌõÖÏùÄ Ìï≠ÏÉÅ Ìò∏Ï∂ú)
  const isLoading = searchdata.state === "loading";
  const isError = searchdata.state === "hasError";

  // droplistdataÏùò Î¶¨Ïä§Ìä∏Îì§ÏùÑ ÏÇ¨Ïö©ÌïòÏó¨ uniqueValues ÏÉùÏÑ±
  const uniqueValues = {
    type: typelist,
    groupname: grouplist,
    turn: turnlist,
    batch: typeidlist,
    customertype: classificationlist,
  };

  const toggleDropdown = (key) => {
    setDropdownOpen((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  const handleFilterChange = (key, value) => {
    setFilters((prevFilters) => {
      const values = prevFilters[key];
      if (values.includes(value)) {
        // Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Í∞íÏù¥Î©¥ Ï†úÍ±∞
        const newValues = values.filter((v) => v !== value);
        return {
          ...prevFilters,
          [key]: newValues,
        };
      } else {
        // ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏùÄ Í∞íÏù¥Î©¥ Ï∂îÍ∞Ä
        const newValues = [...values, value];
        return {
          ...prevFilters,
          [key]: newValues,
        };
      }
    });
  };

  const handleFilterAllChange = (key) => {
    setFilters((prevFilters) => {
      if (prevFilters[key].length === uniqueValues[key].length) {
        // Î™®Îì† ÏòµÏÖòÏù¥ ÏÑ†ÌÉùÎêú ÏÉÅÌÉúÎ©¥ Î™®Îëê Ìï¥Ï†ú
        return {
          ...prevFilters,
          [key]: [],
        };
      } else {
        // ÌïòÎÇòÎùºÎèÑ ÏÑ†ÌÉùÎêòÏßÄ ÏïäÏùÄ ÏòµÏÖòÏù¥ ÏûàÏúºÎ©¥ Î™®Îëê ÏÑ†ÌÉù
        return {
          ...prevFilters,
          [key]: uniqueValues[key].map((option) => option.value),
        };
      }
    });
  };

  const handleSort = (key) => {
    let direction = "ascending";
    if (sortConfig.key === key && sortConfig.direction === "ascending") {
      direction = "descending";
    }
    setSortConfig({ key, direction });
  };

  // ÏïÑÏù¥ÏΩòÏùÑ Î∞òÌôòÌïòÎäî Ìï®Ïàò
  const getSortIcon = (key) => {
    if (sortConfig.key === key) {
      return sortConfig.direction === "ascending" ? (
        <AiOutlineArrowUp size={12} color="#7152F3" />
      ) : (
        <AiOutlineArrowDown size={12} color="#7152F3" />
      );
    }
    return <AiOutlineArrowDown size={12} />;
  };

  const handleReset = () => {
    setFilters({
      type: [],
      groupname: [],
      turn: [],
      batch: [],
      customertype: [],
    });
    setSortConfig({
      key: null,
      direction: "ascending",
    });
  };

  const filteredData = () => {
    let data = isLoading || isError ? [] : [...searchdata.contents];

    // ÌïÑÌÑ∞ Ï†ÅÏö©
    Object.keys(filters).forEach((key) => {
      if (filters[key].length > 0) {
        if (key === "customertype") {
          // ÏÑ†ÌÉùÎêú ÏΩîÎìúÎì§ÏùÑ Ìï¥ÎãπÌïòÎäî Î∂ÑÎ•òÎ™ÖÏúºÎ°ú Î≥ÄÌôò
          const selectedCategories = filters.customertype.map(
            (code) => categoryMapping[code]
          );
          data = data.filter((item) =>
            selectedCategories.includes(categoryMapping[item.customertype])
          );
        } else {
          data = data.filter((item) => {
            const itemValue = item[key];
            return filters[key].includes(itemValue);
          });
        }
      }
    });

    // Ï†ïÎ†¨ Ï†ÅÏö©
    if (sortConfig.key !== null) {
      data.sort((a, b) => {
        let aValue = a[sortConfig.key];
        let bValue = b[sortConfig.key];

        if (sortConfig.key === "id") {
          aValue = parseInt(aValue, 10);
          bValue = parseInt(bValue, 10);
        }

        if (aValue < bValue) {
          return sortConfig.direction === "ascending" ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === "ascending" ? 1 : -1;
        }
        return 0;
      });
    }

    return data;
  };

  const handleDelete = async (id) => {
    try {
      await deleteCustomer(id);
      Swal.fire({
        icon: "success",
        title: "ÌöåÏõê ÏÇ≠Ï†ú",
        text: "ÌöåÏõêÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.",
        confirmButtonText: "ÌôïÏù∏",
      }).then(() => {
        window.location.reload();
      });
    } catch (error) {
      console.error("Error deleting customer:", error);
      Swal.fire({
        icon: "error",
        title: "ÏÇ≠Ï†ú Ïã§Ìå®",
        text: "ÏÇ¨Ïö©Ïûê ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.",
        confirmButtonText: "ÌôïÏù∏",
      });
    }
  };

  const openConfirmation = (id) => {
    setSelectedCustomerId(id);
    setIsModalOpen(true);
  };

  const confirmDelete = () => {
    if (selectedCustomerId !== null) {
      handleDelete(selectedCustomerId);
      setSelectedCustomerId(null);
      setIsModalOpen(false);
    }
  };

  const cancelDelete = () => {
    setSelectedCustomerId(null);
    setIsModalOpen(false);
  };

  // customertype ÌïÑÌÑ∞Í∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ìï¥Îãπ ÌïÑÌÑ∞Ïùò Î∂ÑÎ•òÎ™ÖÍ≥º ÌïÑÌÑ∞Ïóê Ìï¥ÎãπÌïòÎäî ÌöåÏõêÎì§ÏùÑ ÏΩòÏÜîÏóê Ï∂úÎ†•
  useEffect(() => {
    if (!isLoading && !isError && searchdata.state === "hasValue") {
      if (filters.customertype.length > 0) {
        const selectedCategories = filters.customertype.map(
          (code) => categoryMapping[code]
        );
        const filteredMembers = searchdata.contents.filter((customer) =>
          selectedCategories.includes(categoryMapping[customer.customertype])
        );
      }
    }
  }, [filters.customertype, searchdata, isLoading, isError]);

  return (
    <div>
      {isLoading && <div>Loading...</div>}
      {isError && <div>Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.</div>}
      {!isLoading && !isError && (
        <>
          <div className={styles.tablecontainer}>
            {/* ÌÖåÏù¥Î∏î Ìó§Îçî */}
            {/* Í¥ÄÎ¶¨Î≤àÌò∏ */}
            <div className={styles.unitContainer}>
              <span>
                Í¥ÄÎ¶¨Î≤àÌò∏
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("id")}
                >
                  {getSortIcon("id")}
                </span>
              </span>
            </div>
            {/* ÏÑ±Î™Ö */}
            <div className={styles.unitContainer}>
              <span>
                ÏÑ±Î™Ö
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("customerData.name")}
                >
                  {getSortIcon("customerData.name")}
                </span>
              </span>
            </div>
            {/* ÌÉÄÏûÖ */}
            <div className={styles.unitContainer}>
              <span>
                <span onClick={() => toggleDropdown("type")}>ÌÉÄÏûÖ</span>
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("type")}
                >
                  {getSortIcon("type")}
                </span>
              </span>
              {/* ÎìúÎ°≠Îã§Ïö¥ Î©îÎâ¥ */}
              {dropdownOpen.type && (
                <div
                  className={styles.dropdown}
                  onClick={(e) => e.stopPropagation()}
                >
                  <div>
                    <label>
                      <input
                        type="checkbox"
                        checked={
                          filters.type.length === uniqueValues.type.length
                        }
                        onChange={() => handleFilterAllChange("type")}
                      />
                      Ï†ÑÏ≤¥
                    </label>
                  </div>
                  {uniqueValues.type.map((option) => (
                    <div key={option.value}>
                      <label>
                        <input
                          type="checkbox"
                          checked={filters.type.includes(option.value)}
                          onChange={() =>
                            handleFilterChange("type", option.value)
                          }
                        />
                        {option.item}
                      </label>
                    </div>
                  ))}
                </div>
              )}
            </div>
            {/* Íµ∞ */}
            <div className={styles.unitContainer}>
              <span>
                <span onClick={() => toggleDropdown("groupname")}>Íµ∞</span>
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("groupname")}
                >
                  {getSortIcon("groupname")}
                </span>
              </span>
              {dropdownOpen.groupname && (
                <div
                  className={styles.dropdown}
                  onClick={(e) => e.stopPropagation()}
                >
                  <div>
                    <label>
                      <input
                        type="checkbox"
                        checked={
                          filters.groupname.length ===
                          uniqueValues.groupname.length
                        }
                        onChange={() => handleFilterAllChange("groupname")}
                      />
                      Ï†ÑÏ≤¥
                    </label>
                  </div>
                  {uniqueValues.groupname.map((option) => (
                    <div key={option.value}>
                      <label>
                        <input
                          type="checkbox"
                          checked={filters.groupname.includes(option.value)}
                          onChange={() =>
                            handleFilterChange("groupname", option.value)
                          }
                        />
                        {option.item}
                      </label>
                    </div>
                  ))}
                </div>
              )}
            </div>
            {/* ÏàúÎ≤à */}
            <div className={styles.unitContainer}>
              <span>
                <span onClick={() => toggleDropdown("turn")}>ÏàúÎ≤à</span>
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("turn")}
                >
                  {getSortIcon("turn")}
                </span>
              </span>
              {dropdownOpen.turn && (
                <div
                  className={styles.dropdown}
                  onClick={(e) => e.stopPropagation()}
                >
                  <div>
                    <label>
                      <input
                        type="checkbox"
                        checked={
                          filters.turn.length === uniqueValues.turn.length
                        }
                        onChange={() => handleFilterAllChange("turn")}
                      />
                      Ï†ÑÏ≤¥
                    </label>
                  </div>
                  {uniqueValues.turn.map((option) => (
                    <div key={option.value}>
                      <label>
                        <input
                          type="checkbox"
                          checked={filters.turn.includes(option.value)}
                          onChange={() =>
                            handleFilterChange("turn", option.value)
                          }
                        />
                        {option.item}
                      </label>
                    </div>
                  ))}
                </div>
              )}
            </div>
            {/* Í∞ÄÏûÖ Ï∞®Ïàú */}
            <div className={styles.unitContainer}>
              <span>
                <span onClick={() => toggleDropdown("batch")}>Í∞ÄÏûÖ Ï∞®Ïàú</span>
                <span
                  className={styles.sortIcon}
                  onClick={() => handleSort("batch")}
                >
                  {getSortIcon("batch")}
                </span>
              </span>
              {dropdownOpen.batch && (
                <div
