__files__.api.js:
// src/utils/api.js
import axios from "axios";

const path = "http://localhost:8080";
//const path = "http://3.38.181.18:8080";

// Í≥†Í∞ù Ï∂îÍ∞Ä ÌéòÏù¥ÏßÄ ÏÉàÎ°úÏö¥ ÏïÑÏù¥Îîî Î∞õÏïÑÏò§Í∏∞
export const newIdGenerate = () => {
  return axios
    .get(`${path}/customers/nextId`)
    .then((result) => result.data)
    .catch((error) => {
      console.error(error);
      throw error;
    });
};

// ÌååÏùº ÏóÖÎ°úÎìú (ÌååÏùºÎ™Ö Î≥ÄÍ≤Ω Ìè¨Ìï®)
export const createFile = (file, custId) => {
  const formData = new FormData();
  const originalName = file.name;
  const extension = originalName.split(".").pop();
  const baseName = originalName.substring(0, originalName.lastIndexOf("."));
  const newFileName = `${custId}_${baseName}.${extension}`;
  formData.append("file", file, newFileName);
  return axios.post(`${path}/files/upload`, formData, {
    headers: { "Content-Type": "multipart/form-data" },
  });
};

export const downloadFile = async (id, filename) => {
  try {
    const response = await axios.get(`${path}/files/download`, {
      params: { id, filename },
      responseType: "blob",
    });
    const disposition = response.headers["content-disposition"];
    let fileName = "downloaded_file";
    if (disposition && disposition.indexOf("filename=") !== -1) {
      const filenameRegex = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/;
      const matches = filenameRegex.exec(disposition);
      if (matches != null && matches[1]) {
        fileName = matches[1].replace(/['"]/g, "");
      }
    }
    const blob = new Blob([response.data], {
      type: response.headers["content-type"],
    });
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.setAttribute("download", fileName);
    document.body.appendChild(link);
    link.click();
    link.remove();
    window.URL.revokeObjectURL(url);
  } catch (error) {
    console.error("Error downloading file:", error);
    throw error;
  }
};

// Í≥†Í∞ù ÏÉùÏÑ±
export const createUser = (data) => {
  return axios.post(`${path}/customers`, data);
};

// Í≥†Í∞ù Í≤ÄÏÉâ
export const fetchCustomers = (params) => {
  return axios
    .get(`${path}/customers/search`, { params })
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching customers:", error);
      throw error;
    });
};

// Í≥†Í∞ù ÏÇ≠Ï†ú
export const deleteCustomer = (id) => {
  return axios
    .delete(`${path}/customers/${id}`)
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error deleting customer:", error);
      throw error;
    });
};

// Í≥†Í∞ù ÏÉÅÏÑ∏ Ï°∞Ìöå
export const fetchCustomerById = (id) => {
  return axios
    .get(`${path}/customers/${id}`)
    .then((response) => response.data)
    .catch((error) => {
      console.error("Error fetching customer:", error);
      throw error;
    });
};

// ÎÇ©Î∂Ä Ï†Ñ Ï∞®Ïàò Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
