// JWT ÌÜ†ÌÅ∞ Ìè¨Ìï® ÏöîÏ≤≠ÏùÑ ÏúÑÌïú Axios Ïù∏ÌÑ∞ÏÖâÌÑ∞ ÏÑ§Ï†ï
axios.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem("token");
    if (token) {
      config.headers["Authorization"] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

