export async function uploadExcelFileWithProgress(
  file,
  onProgress,
  onComplete,
  onError
) {
  const formData = new FormData();
  formData.append("file", file);

  // Î∞±ÏóîÎìú URL (ÏßÅÏ†ë ÏÉÅÏàò ÌòπÏùÄ env Îì±)
  const backendUrl = `${path}/files/uploadExcelWithProgress`;

  // fetchÎ°ú POST
  const response = await fetch(backendUrl, {
    method: "POST",
    headers: {
      // SSE Ïä§Ìä∏Î¶ºÏùÑ Î∞õÍ∏∞ ÏúÑÌï¥ Accept Ìó§Îçî ÏÑ§Ï†ï
      Accept: "text/event-stream",
    },
    body: formData,
  });

  if (!response.ok) {
    throw new Error("ÏÑúÎ≤Ñ ÏùëÎãµ ÏóêÎü¨: " + response.statusText);
  }

  // Ïä§Ìä∏Î¶º(body)Ïù¥ ÏóÜÏúºÎ©¥ ÏóêÎü¨
  if (!response.body) {
    throw new Error("ReadableStreamÏùÑ Í∞ÄÏ†∏Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§");
  }

  // Ïä§Ìä∏Î¶º ‚Üí reader
  const reader = response.body.getReader();
  const decoder = new TextDecoder("utf-8");

  let buffer = "";
  let currentEvent = "";

  // Î¨¥Ìïú Î£®ÌîÑ: chunk Îã®ÏúÑÎ°ú SSE Ï≤òÎ¶¨
  while (true) {
    const { done, value } = await reader.read();
    if (done) break;

    // chunk ‚Üí Î¨∏ÏûêÏó¥
    buffer += decoder.decode(value, { stream: true });

    // SSEÎäî \n\n Î°ú Ïù¥Î≤§Ìä∏ Í≤ΩÍ≥Ñ, Ïö∞ÏÑ† Ï§Ñ Îã®ÏúÑÎ°ú split
    const lines = buffer.split(/\r?\n/);

    // ÎßàÏßÄÎßâ Ï§Ñ Ï†ÑÍπåÏßÄ ÌååÏã±
    for (let i = 0; i < lines.length - 1; i++) {
      const line = lines[i].trim();

      if (!line) {
        // Îπà Ï§Ñ == Ïù¥Î≤§Ìä∏ Íµ¨Î∂Ñ
        currentEvent = "";
        continue;
      }

      if (line.startsWith("event:")) {
        currentEvent = line.slice("event:".length).trim();
      } else if (line.startsWith("data:")) {
        const data = line.slice("data:".length).trim();
        // Ïù¥Î≤§Ìä∏Î≥Ñ Ï≤òÎ¶¨
        if (currentEvent === "progress" && onProgress) {
          onProgress(data); // Ïòà: "3/30"
        } else if (currentEvent === "complete" && onComplete) {
          onComplete(data); // Ïòà: "Parsing complete"
        } else if (currentEvent === "error" && onError) {
          onError(data); // Ïòà: "Some error occurred"
        }
      }
    }
    // ÎßàÏßÄÎßâ Ï§Ñ ÎÇ®ÍπÄ
    buffer = lines[lines.length - 1];
  }

  // ÎÇ®ÏùÄ Î∂ÄÎ∂Ñ Ï≤òÎ¶¨
  if (buffer) {
    const line = buffer.trim();
    if (line.startsWith("event:")) {
      currentEvent = line.slice("event:".length).trim();
    } else if (line.startsWith("data:")) {
      const data = line.slice("data:".length).trim();
      if (currentEvent === "progress" && onProgress) {
        onProgress(data);
      } else if (currentEvent === "complete" && onComplete) {
        onComplete(data);
      } else if (currentEvent === "error" && onError) {
        onError(data);
      }
    }
  }
}

// SSE ÏóîÎìúÌè¨Ïù∏Ìä∏Î•º ÌÜµÌï¥ ÏßÑÌñâ ÏÉÅÌô©Í≥º ÏµúÏ¢Ö fileIdÎ•º Î∞õÍ≥†, Í∑∏ ÌõÑ Ïã§Ï†ú ÌååÏùº Îã§Ïö¥Î°úÎìú ÏöîÏ≤≠ÏùÑ ÌïòÎäî Ìï®Ïàò
export const downloadRegFile = async (onProgress, onComplete, onError) => {
  try {
    const eventSource = new EventSource(
      `${path}/files/regfiledownload/progress`
    );

    eventSource.addEventListener("progress", (event) => {
      if (onProgress) {
        onProgress(event.data); // Ïòà: "3/48"
      }
    });

    eventSource.addEventListener("complete", async (event) => {
      // complete Ïù¥Î≤§Ìä∏ Îç∞Ïù¥ÌÑ∞ÏóêÎäî fileIdÍ∞Ä Îì§Ïñ¥ÏûàÏùå
      const fileId = event.data;
      try {
        const response = await axios.get(
          `${path}/files/regfiledownload/file?fileId=${fileId}`,
          {
            responseType: "blob",
          }
        );
        const disposition = response.headers["content-disposition"];
        let fileName = "regfile_download.xlsx";
        if (disposition && disposition.indexOf("filename=") !== -1) {
          const filenameRegex = /filename[^;=\n]*=((['"]).*?\2|[^;\n]*)/;
          const matches = filenameRegex.exec(disposition);
          if (matches != null && matches[1]) {
            fileName = matches[1].replace(/['"]/g, "");
          }
        }
        const blob = new Blob([response.data], {
          type: response.headers["content-type"],
        });
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", fileName);
        document.body.appendChild(link);
        link.click();
        link.remove();
        window.URL.revokeObjectURL(url);
        if (onComplete) {
          onComplete(fileName);
        }
      } catch (downloadError) {
        if (onError) {
          onError(downloadError);
        }
      } finally {
        eventSource.close();
      }
    });

    eventSource.addEventListener("error", (event) => {
      if (onError) {
        onError("SSE Ïó∞Í≤∞ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
      eventSource.close();
    });
  } catch (error) {
    if (onError) {
      onError(error);
    }
  }
};

